from typing import List

class Solution:
    def separateSquares(self, squares: List[List[int]]) -> float:
        """Find the minimum y such that total area below equals total area above."""
        if not squares:
            return 0.0

        total_area = 0.0
        min_y = float('inf')
        max_y = -float('inf')
        for x, y, l in squares:
            total_area += l * l
            if y < min_y:
                min_y = y
            top = y + l
            if top > max_y:
                max_y = top

        target = total_area / 2.0

        def area_below(Y: float) -> float:
            s = 0.0
            for x, y, l in squares:
                if Y <= y:
                    continue
                elif Y >= y + l:
                    s += l * l
                else:
                    s += l * (Y - y)
            return s

        lo = min_y
        hi = max_y
        # Binary search for minimal Y with area_below(Y) >= target
        for _ in range(80):
            mid = (lo + hi) / 2.0
            if area_below(mid) >= target:
                hi = mid
            else:
                lo = mid

        return hi

