#!/usr/bin/env python3
import sys
import math

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    D = int(data[0])
    ans = D
    max_x = math.isqrt(D)
    for x in range(max_x + 1):
        x2 = x * x
        r = D - x2
        y = math.isqrt(r)
        for yy in (y, y + 1):
            if yy < 0:
                continue
            val = x2 + yy * yy
            diff = val - D if val >= D else D - val
            if diff < ans:
                ans = diff
        if ans == 0:
            break
    print(ans)

if __name__ == '__main__':
    main()

